mapper.update:
UPDATE
	TB_BUY_MST
SET
	CD_STATE			= #{cd_state}
	, CD_STATE_PAY		= #{cd_state_pay}
	, CD_STATE_DELIVERY	= #{cd_state_delivery}
	, UPDATER			= #{updater}
WHERE
	SEQ_BUY_MST = #{seq_buy_mst}

mapper.select:
SELECT * FROM ( SELECT /*+ first_rows(10) */ ROW_NUMBER() OVER (ORDER BY buy_mst.DT_REG DESC) rnum , mbr_mst.SEQ_MBR , mbr_mst.EMAIL , mbr_mst.MBR_NM , buy_mst.SEQ_BUY_MST , buy_mst.BUY_INFO , buy_mst.BUY_COUNT , buy_mst.CD_STATE , buy_mst.CD_STATE_PAY , buy_mst.CD_STATE_DELIVERY , TO_CHAR(buy_mst.DT_REG, 'YYYY-MM-DD') dt_reg FROM TB_BUY_MST buy_mst LEFT JOIN TB_MBR_MST mbr_mst ON buy_mst.SEQ_MBR = mbr_mst.SEQ_MBR WHERE
)
WHERE rnum >= (${currentPage} - 1) * ${linePerPage} + 1 AND rnum <= ${currentPage} * ${linePerPage}
ORDER BY rnum
