mapper.select:
SELECT buy_mst.SEQ_MBR, buy_dtl.SLE_NM, buy_dtl.COUNT, buy_dtl.PRICE, buy_dtl.POINT, buy_dtl.FLG_REVIEW, TO_CHAR(buy_dtl.DT_REG, 'YYYY-MM-DD') AS dt_reg FROM TB_BUY_MST buy_mst LEFT JOIN TB_BUY_DTL buy_dtl ON buy_mst.SEQ_BUY_MST = buy_dtl.SEQ_BUY_MST WHERE 1 = 1
AND buy_dtl.CD_STATE = #{searchCdState}
AND buy_dtl.CD_STATE_PAY = #{searchCdStatePay}
AND buy_dtl.CD_STATE_DELIVERY = #{searchCdStateDelivery}

mapper.update:
UPDATE
	TB_BUY_MST
SET
	CD_STATE			= #{cd_state}
	, CD_STATE_PAY		= #{cd_state_pay}
	, CD_STATE_DELIVERY	= #{cd_state_delivery}
	, UPDATER			= #{updater}
WHERE
	SEQ_BUY_MST = #{seq_buy_mst}

mapper.select:
