mapper.select:
SELECT * FROM ( SELECT /*+ first_rows(10) */ ROW_NUMBER() OVER (ORDER BY DT_REG DESC) rnum , SEQ_SLE , SLE_NM , CORP_NM , CD_STATE_SALE , PRICE_SALE , TO_CHAR(DT_REG, 'YYYY-MM-DD') dt_reg FROM TB_SLE WHERE
)
WHERE rnum >= (${currentPage} - 1) * ${linePerPage} + 1 AND rnum <= ${currentPage} * ${linePerPage}
ORDER BY rnum

mapper.sql:
	REGISTER = #{register}

	
		AND UPPER(SLE_NM) LIKE UPPER('%' || #{searchWord} || '%')
	
	
		AND UPPER(CORP_NM) LIKE UPPER('%' || #{searchWord} || '%')
	
	
		AND (
			UPPER(CORP_NM) LIKE UPPER('%' || #{searchWord} || '%')
			OR
			UPPER(SLE_NM) LIKE UPPER('%' || #{searchWord} || '%')
		)
	


	
		AND CD_STATE_SALE = #{cd_state_sale}
	
	
		AND CD_STATE_SALE IN (1, 2, 3, 9)
	


	
		AND CD_CTG_B = #{cd_ctg_b}
	
	
		AND cd_ctg_b IN (1, 2, 3)
	


	
		AND CD_CTG_M = #{cd_ctg_m}
	
	
		AND cd_ctg_m IN (1, 2, 3, 4, 5, 6, 7, 8, 9)
