mapper.update:
UPDATE
	TB_BSK
SET
	COUNT			= #{count}
	, DT_UPT		= SYSDATE
WHERE
	SEQ_MBR			= #{seq_mbr}
	AND SEQ_SLE		= #{seq_sle}
	AND FLG_DELETE	= 'N'

UPDATE
	TB_BSK
SET
	PRICE			= PRICE + #{price}
	, COUNT			= COUNT + #{count}
	, POINT_STACK	= #{point_stack}
	, DT_UPT		= SYSDATE
WHERE
	SEQ_MBR			= #{seq_mbr}
	AND SEQ_SLE		= #{seq_sle}
	AND FLG_DELETE	= 'N'

mapper.select:
SELECT
	COUNT(SEQ_SLE)
FROM
	TB_BSK
WHERE
	SEQ_MBR			= #{seq_mbr}
	AND SEQ_SLE		= #{seq_sle}
	AND CD_CTG_M	= #{cd_ctg_m}
	AND CD_CTG_B	= #{cd_ctg_b}
	AND FLG_DELETE	= 'N'
