mapper.select:
SELECT * FROM ( SELECT /*+ first_rows(10) */ ROW_NUMBER() OVER (ORDER BY FLG_TOP, FLG_REPLY DESC, DT_REG DESC) rnum , SEQ_BBS , (SELECT tb_reply.SEQ_BBS FROM TB_BBS tb_reply WHERE tb_reply.SEQ_BBS_PARENT = TB_BBS.SEQ_BBS) seq_reply , DECODE(CD_CTG, 1, '가입탈퇴', 2, '상품', 3, '구매', 4, '결제', 5, '배송', 6, '환불', 9, '기타', '미정') CTG_NM , TITLE , FILE_ORIG , DECODE(LOWER(SUBSTR(FILE_ORIG, INSTR(FILE_ORIG, '.', -1, 1) + 1)) , 'xlsx', 'xls' , 'xls' , 'xls' , 'docx', 'doc' , 'doc' , 'doc' , 'pptx', 'ppt' , 'ppt' , 'ppt' , 'hwp' , 'hwp' , 'pdf' , 'pdf' , NULL , '' , 'etc') extension , FLG_TOP , TO_CHAR(DT_REG, 'YYYY-MM-DD') dt_reg , READED FROM TB_BBS WHERE
)
WHERE rnum >= (${currentPage} - 1) * ${linePerPage} + 1 AND rnum <= ${currentPage} * ${linePerPage}
ORDER BY FLG_TOP, rnum

mapper.sql:
CD_BBS_TYPE = #{cd_bbs_type}
AND FLG_DELETE IS NULL

	
		AND TITLE LIKE '%' || #{searchWord} || '%'
	
	
		AND CONTENT LIKE '%' || #{searchWord} || '%'
	
	
		AND (TITLE LIKE '%' || #{searchWord} || '%' OR CONTENT LIKE '%' || #{searchWord} || '%')
	


	AND CD_CTG = #{cd_ctg}


	AND SEQ_BBS_PARENT IS NULL
	AND REGISTER = #{register}
