mapper.update:
UPDATE
	TB_MBR_ADDR
SET
	FLG_DELETE	= 'Y'
	, DT_UPT	= SYSDATE
	, UPDATER	= #{seq_mbr}
WHERE
	SEQ_MBR		= #{seq_mbr}
	AND SEQ_MBR_ADDR = #{seq_mbr_addr}

UPDATE
	TB_MBR_ADDR
SET
	POST		= #{post}
	, ADDR1		= #{addr1}
	, ADDR2		= #{addr2}
	, DT_UPT	= SYSDATE
	, UPDATER	= #{seq_mbr}
WHERE
	SEQ_MBR		= #{seq_mbr}
	AND SEQ_MBR_ADDR = #{seq_mbr_addr}

UPDATE
	TB_MBR_MST
SET
	PASSWD		= #{passwd}
	, UPDATER	= #{updater}
	, DT_UPT	= SYSDATE
WHERE
	SEQ_MBR		= #{seq_mbr}

UPDATE
	TB_MBR_MST
SET
	TOTAL_POINT = NVL(TOTAL_POINT, 0) + #{total_point}
	, DT_UPT	= SYSDATE
	, UPDATER	= #{seq_mbr}
WHERE
	SEQ_MBR		= #{seq_mbr}

UPDATE
	TB_MBR_MST
SET
	TOTAL_POINT	= TOTAL_POINT - #{use_point}
	, DT_UPT	= SYSDATE
	, UPDATER	= #{seq_mbr}
WHERE
	SEQ_MBR		= #{seq_mbr}

UPDATE
	TB_MBR_MST
SET
	DT_UPT	= SYSDATE
	
	, PHONE	= #{phone}
	
	, UPDATER = #{updater}
WHERE
	SEQ_MBR	= #{seq_mbr}

UPDATE
	TB_MBR_DTL
SET
	DT_UPT		= SYSDATE
	, UPDATER	= #{updater}
	
	, POST		= #{post}
	
	
	, ADDR1		= #{addr1}
	
	
	, ADDR2		= #{addr2}
	
	
	, FLG_SMS	= #{flg_sms}
	, DT_SMS	= SYSDATE
	
	
	, FLG_EMAIL	= #{flg_email}
	, DT_EMAIL	= SYSDATE
	
WHERE
	SEQ_MBR		= #{seq_mbr}

UPDATE
	TB_MBR_MST
SET
	PASSWD		= #{passwd}
	, DT_UPT	= SYSDATE
WHERE
EMAIL = #{email}
AND MBR_NM = #{mbr_nm}
