SELECT * FROM ( SELECT /*+ first_rows(10) */ ROW_NUMBER() OVER (ORDER BY sle.DT_REG DESC) rnum , sle.SEQ_SLE , sle.SLE_NM , sle.IMG , sle.PRICE_SALE , sle.CD_CTG_B , sle.CD_CTG_M , rate.RATE_STAR , rate.COUNT , ROUND(rate.RATE_STAR * 1.0 / rate.COUNT, 0) average_rate , sle.PRD_TYPE , sle.CORP_NM , sle.DISCOUNT , sle.PRICE_SALE * (1 - (sle.DISCOUNT / 100)) discount_sale , TO_CHAR(sle.DT_REG, 'YYYY-MM-DD') DT_REG FROM TB_SLE sle LEFT JOIN TB_RATE rate ON sle.SEQ_SLE = rate.SEQ_SLE WHERE
)
WHERE rnum >= (${currentPage} - 1) * ${linePerPage} + 1 AND rnum <= ${currentPage} * ${linePerPage}
ORDER BY rnum

